mapper.select:
SELECT * FROM (SELECT u.nick_name as nick_name, u.account as account, d.dot_name as dot_name, d.dot_num as dot_num, wlg.group_name as group_name, wlg.id as id, wlg.group_leader as group_leader, wl.today_work as today_work, wl.today_good as today_good, wl.today_bad as today_bad, wl.tomorrow_plan as tomorrow_plan, wl.tomorrow_promise as tomorrow_promise, wl.create_time as create_time
FROM zh_work_log wl
LEFT JOIN zh_users u ON wl.create_user_code = u.account
LEFT JOIN zh_dot d ON u.station_id = d.dot_num
LEFT JOIN zh_work_log_group wlg ON u.group_id = wlg.id WHERE wlg.is_delete = '0'
) as tb WHERE 1 = 1
AND tb.id = #{id}
AND tb.create_time BETWEEN #{startTime} AND #{endTime}
LIMIT #{limit},#{offset}

SELECT COUNT(*) FROM (SELECT * FROM (SELECT u.nick_name as nick_name, u.account as account, d.dot_name as dot_name, d.dot_num as dot_num, wlg.group_name as group_name, wlg.id as id, wlg.group_leader as group_leader, wl.today_work as today_work, wl.today_good as today_good, wl.today_bad as today_bad, wl.tomorrow_plan as tomorrow_plan, wl.tomorrow_promise as tomorrow_promise, wl.create_time as create_time
FROM zh_work_log wl
LEFT JOIN zh_users u ON wl.create_user_code = u.account
LEFT JOIN zh_dot d ON u.station_id = d.dot_num
LEFT JOIN zh_work_log_group wlg ON u.group_id = wlg.id WHERE wlg.is_delete = '0'
) as tb WHERE 1 = 1
AND tb.id = #{id}
AND tb.create_time BETWEEN #{startTime} AND #{endTime}
) as coo
